balances=`curl -s http://richlist.btsgame.org/$1.json | ./asset-holders.js | while read p; do balance=$(./get-balance.js $p $3); printf "$p:$balance\n"; done`; sum=`printf "$balances" | awk -F":" -v s="$4" '{s+=$2} END {print s}'`; printf "$balances" | awk -v CONVFMT=%.2f -v from="$2" -v profit="$5" -v sum="$sum" -F":" '{print "transfer " from " "$1" "$2/sum*profit " CNY 0 true"}'
