balances=`curl -s http://richlist.btsgame.org/$1.json | ./asset-holders.js | while read p; do balance=$(./get-balance.js $p $3 $4); printf "$p:$balance\n"; done`; sum=`printf "$balances" | awk -F":" -v s="$5" '{s+=$2} END {print s}'`; printf "$balances" | awk -v CONVFMT=%.2f -v from="$2" -v profit="$6" -v sum="$sum" -v asset="$1" -F":" '{print "transfer " from " "$1" "$2/sum*profit " CNY '\''" asset " dividends'\'' true"}'
