balances=`curl -s http://richlist.btsgame.org/botfund.json | ./asset-holders.js | while read p; do balance=$(./get-balance.js $p 1.3.289); printf "$p:$balance\n"; done`; sum=`printf "$balances" | awk -F":" -v s="$1" '{s+=$2} END {print s}'`; printf "$balances" | awk -v CONVFMT=%.2f -v profit="$2" -v sum="$sum" -F":" '{print "transfer btsjohn "$1" "$2/sum*profit " CNY 0 true"}'
